    class Button extends React.Component{
        constructor(props){
            super(props)
        };
        onHandleClick=()=>{
           return this.props.color
        }


        render (){
            return (
                <div>
                <button onClick={()=> this.props.onHandleClick}>{this.props.color} </button>
                </div>
                );
        }
    }
    export default Button;
