import items from '../../Store';


class ProductsDetails extends React.Component {

    constructor(props){
        super(props);
        this.state={ itemsList: items, currentItem:'0'}
      
    }
 
    componentDidMount() {
        const id=this.props.match.params.id
        this.setState({currentItem:id})
    }

     displayProducts=()=>{

        const prod=this.state.items.find(item => item.id===this.state.currentItem)
      
              console.log("found it",prod)
      }
       
    
     
    

    render() {
        this.displayProducts();
