const colors = ['blue','red','yellow'];

    class App extends React.Component{

        // state={isLoading:false, isOpen: true};

        componentDidMount() {
           
          }

           displayButtons = () => {
            return colors.map((button) => {
              return <Button buttonColor={button}/>;
            });
          };

        render(){
            return (
            <div> 
                {this.displayButtons()}
                 

            </div>
            )
        }
    }
    export default App;
