    class Button extends React.Component{
        constructor(props){
            super(props)
        };

        handleClick(event){
            this.props.parentCallback(event.target.value)
        }
        render (){
            return (
                <div>
                <button onClick={()=>props.onHandleClick("hello")}>  </button>
                </div>
                );
        }
    }
    export default Button;
